mapper.update:
UPDATE FREE_BOARD
SET	TITLE = #{title}, B_CONTENT = #{bContent}, MDT = SYSDATE
WHERE BOARD_NO = #{boardNo}

UPDATE FREE_BOARD
SET BOARD_STATUS = 'N'
WHERE BOARD_NO = #{boardNo}

UPDATE FREE_COMMENT
SET COMMENT_STATUS = 'N'
WHERE COMMENT_NUM = #{commentNum}

UPDATE FREE_COMMENT
SET C_CONTENT = #{cContent}, MDT = SYSDATE
WHERE COMMENT_NUM = #{commentNum}
